const SET_USER_DATA = 'SET_USER_DATA'


const initialState1 = {
    avatar_url: '',
    login: '',
    name: '',
    followers: null,
    following: null
}

const initialState = {
    user: {}
}


export const userReducer = (state = initialState, action) => {
    switch (action.type) {
        case SET_USER_DATA:
            return {...state, user: {...action.payload}}

        default:
            return {...state}

    }
}

export const setUserData = (data) => {
    return {
        type: SET_USER_DATA,
        payload: data
    }
}
