import React from 'react'
import './Repositories.css'
import {useDispatch, useSelector} from "react-redux";
import {getRepos} from "../../store/reducers/user";
import {Repository} from "./Repository/Repository";
import ReactPaginate from 'react-paginate'


export const Repositories = () => {
    const state = useSelector(state => state.user)
    const dispatch = useDispatch();
    let pageCount = Math.ceil(state.user.public_repos / 4)
    let pages = []
    for (let i = 1; i <= pageCount; i++) {
        pages.push(i)
    }

    const onClicker = (page) => {
        dispatch(getRepos(page, 4, state.name))
    }

    return (
        <div className='repo-container'>
            <div className='repo-header'>Repositories({state ? state.user.public_repos : ''})</div>
            {state.repo.map(repo => {
                return (
                    <Repository name={repo.name} description={repo.description} url={repo.html_url}/>
                )
            })}
            {pages.map(page => {
                return <ReactPaginate
                    previousLabel={'previous'}
                    nextLabel={'next'}
                    breakLabel={'...'}
                    breakClassName={'break-me'}
                    pageCount={pages.length}
                    marginPagesDisplayed={2}
                    pageRangeDisplayed={5}
                    onPageChange={() => onClicker(page)}
                    containerClassName={"pagination"}
                    previousLinkClassName={"pagination__link"}
                    nextLinkClassName={"pagination__link"}
                    disabledClassName={"pagination__link--disabled"}
                    activeClassName={"pagination__link--active"}
                />
            })
            }

            {/*pages.map(page => {
                return <span onClick={()=>onClicker(page)}>{page}</span>
            })
            */}

        </div>
    )
}
