import React, {useState} from 'react'
import './Header.css'
import axios from "axios";
import {useDispatch} from "react-redux";
import {setUserData} from "../../store/reducers/user";


export const Header = () => {

    const dispatch = useDispatch()

    const initialState = {
        avatar_url: '',
        login: '',
        name: '',
        followers: null,
        following: null
    }

    const [nick, setNick] = useState('')
    //const [userData, setUserData] = useState(initialState)

    const inputOnChangeHandler = (e) => {
        setNick(e.target.value)
    }

    /*const onSubmitHandler = (e) => {
        e.preventDefault()
        axios.get(`https://api.github.com/users/${nick}`).then(res => setUserData(prevState => {
            return {
                ...prevState,
                avatar_url: res.data.avatar_url,
                login: res.data.login,
                name: res.data.name,
                followers: res.data.followers,
                following: res.data.following
            }
        }))
        setNick('')
    }*/

    const onSubmitHandler = (e) => {
        e.preventDefault()
        axios.get(`https://api.github.com/users/${nick}`).then(res => dispatch(setUserData(res.data)))
        setNick('')
    }

    return (
        <div className='header-container'>
            <div>Logo</div>
            <form onSubmit={onSubmitHandler}>
                <div>
                    <input onChange={inputOnChangeHandler} value={nick} type="text"/>
                </div>
            </form>
        </div>
    )
}
