import axios from "axios";

const SET_USER_DATA = 'SET_USER_DATA'
const SET_USER_REPO = 'SET_USER_REPO'
const SET_USER_NAME = 'SET_USER_NAME'
const SET_REPO_PAGE = 'SET_REPO_PAGE'


const initialState1 = {
    avatar_url: '',
    login: '',
    name: '',
    followers: null,
    following: null
}

const initialState = {
    user: {},
    repo: [],
    page: 1,
    name: ''
}


export const userReducer = (state = initialState, action) => {
    switch (action.type) {
        case SET_USER_DATA:
            return {...state, user: {...action.payload}}
        case SET_USER_REPO:
            return {...state, repo: [...action.payload]}
        case SET_REPO_PAGE:
            return {...state, page: action.payload}
        case SET_USER_NAME:
            return {...state, name: action.payload}

        default:
            return {...state}

    }
}

export const setUserData = (data) => {
    return {
        type: SET_USER_DATA,
        payload: data
    }
}

export const setUserRepo = (data) => {
    return {
        type: SET_USER_REPO,
        payload: data
    }
}

export const setReposPage = (page) => {
    return {
        type: SET_REPO_PAGE,
        payload: page
    }
}

export const getRepos = (page = 1, limit = 4, nick) => {
    return (dispatch) => {
        axios.get(`https://api.github.com/users/${nick}/repos`, {
            params: {
                page: page,
                per_page: limit
            }
        }).then(res => dispatch(setUserRepo(res.data)))

    }
}
