    }

    return (
        <div className=''>
            <div>Repositories({state ? state.user.public_repos : ''})</div>
            {state.repo.map(repo => {
                return (
                    <div className='repo'>
                        <div>{repo.name}</div>
                        <div>{repo.description}</div>
                    </div>
                )
            })}
            {pages.map(page => {
                return <span onClick={() => dispatch(setReposPage(page))}>{page}</span>
            })
            }
        </div>
    )
}
