import React, {useEffect, useMemo, useState} from 'react'
import './Header.css'
import axios from "axios";
import {useDispatch, useSelector} from "react-redux";
import {getRepos, setUserData, setUserRepo} from "../../store/reducers/user";


export const Header = () => {
    const dispatch = useDispatch()
    const {page} = useSelector(state => state.user)
    const [nick, setNick] = useState('')

    console.log(getRepos(page, 4, nick))

    /*useEffect(()=>{
        dispatch(getRepos(page, 4, nick))
    }, [page, nick])*/

   /* const Memo = useMemo(()=> {
        getRepos(page, 4, nick)
    },[page])*/

    //console.log(Memo)





    const inputOnChangeHandler = (e) => {
        setNick(e.target.value)
    }

    const onSubmitHandler = (e) => {
        e.preventDefault()
        axios.get(`https://api.github.com/users/${nick}`).then(res => dispatch(setUserData(res.data)))
        //dispatch(getRepos(page, 4, nick))
        //axios.get(`https://api.github.com/users/${nick}/repos`, {params: {page: 2, per_page: 4}}).then(res => dispatch(setUserRepo(res.data)))
        //setNick('')
    }

    return (
        <div className='header-container'>
            <div>Logo</div>
            <form onSubmit={onSubmitHandler}>
                <div>
                    <input onChange={inputOnChangeHandler} value={nick} type="text"/>
                </div>
            </form>
        </div>
    )
}
